def is_prime(n, k=5):
    if n <= 1 or n % 2 == 0:
        return False
    for _ in range(k):
        a = random.randint(2, n - 2)
        x = pow(a, n - 1, n)
        if x == 1 or x == n - 1:
            continue
        for _ in range(k - 1):
            x = pow(x, 2, n)
            if x == n - 1:
                break
        else:
            return False
    return True
def largest_prime_factor(n):
    if not is_prime(n):
        return 1
    return n
print(largest_prime_factor(24))  # Output: 8
