def longest_substring(s1, s2):
    n1 = len(s1)
    n2 = len(s2)
    dp = [[0] * (n2 + 1) for _ in range(n1 + 1)]
    for i in range(n1 + 1):
        for j in range(n2 + 1):
            if i == 0 or j == 0:
                dp[i][j] = 0
            else:
                dp[i][j] = max(dp[i - 1][j - 1] + 1, dp[i - 1][j] + 1, dp[i][j - 1] + 1)
    return dp[n1][n2]

def longest_common_prefix(s1, s2):
    n1 = len(s1)
    n2 = len(s2)
    dp = [0] * (n1 + 1)
    for i in range(n1 + 1):
        for j in range(n2 + 1):
            if i == 0 or j == 0:
                dp[i][j] = 0
            else:
                dp[i][j] = dp[i - 1][j - 1] + 1
    return dp[n1][n2]

