from heapq import heapify, heappop, heappush
def timsort(arr):
    n = len(arr)
    if n <= 1:
        return arr
    for i in range(n-1):
        heapify(arr, i)
        for j in range(0, n-i-1):
            heappop(arr, 0)
        for j in range(n-i-1, n-1):
