def max_path_sum(root):
    if not root:
        return 0
    max_sum = -1
    for child in root.children:
        temp_sum = max_sum + child.val
        if temp_sum > max_sum:
            max_sum = temp_sum
    return max_sum

def max_path_sum(root):
    queue = deque([root])
    max_sum = 0
    while queue:
        node = queue.popleft()
        if not node.children:
            continue
        for child in node.children:
            temp_sum = max_sum + child.val
            if temp_sum > max_sum:
                max_sum = temp_sum
                queue.append(child)
    return max_sum

def max_path_sum(root):
    dp = {root: root.val}
    for child in root.children:
        dp[child] = max(dp[child], dp[root] + child.val)
    return dp[root]

