def is_prime(num):
    try:
        if num < 2:
            raise ValueError("Invalid number")
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                raise ValueError("Not prime")
        return True
    except ValueError:
        return False
numbers = [2, 3, 5, 7, 11, 13, 17, 19]
for num in numbers:
    if is_prime(num):
        print(f"{num} is prime")
    else:
        print(f"{num} is composite")
