def find_sum_efficient(arr, target):
    slow, fast = 0, len(arr) - 1
    while fast > 0:
        mid = (slow + fast) // 2
        if arr[mid] + arr[fast] == target:
            return [arr[mid], arr[fast]]
        elif arr[mid] + arr[fast] < target:
            slow = mid + 1
        else:
            fast = mid - 1
