def prime_factors_sieve(n):
    factors = []
    is_prime = [True] * (n + 1)
    for p in range(2, int(n ** 0.5) + 1):
        if is_prime[p]:
            for i in range(p * p, n + 1, p):
                is_prime[i] = False
    for i in range(n + 1):
        if is_prime[i]:
            factors.append(i)
    return factors
# Example usage
