def matrix_multiply(A, B):
    m1 = len(A)
    n1 = len(A[0])
    m2 = len(B)
    n2 = len(B[0])
    C = [[0] * n2 for _ in range(m1)]
    for i in range(m1):
        for j in range(n1):
            for k in range(n2):
                C[i][k] = 0
                for l in range(n1):
                    C[i][k] += A[i][l] * B[l][k]
    return C

