import re
def porter_stemmer(word):
    if len(word) < 3:
        return word
    if word.endswith('isy'):
        return word[:-3] + 'y'
    if word.endswith('s'):
        return word[:-2] + 's'
    if word.endswith('ed'):
        return word[:-2] + 'ing'
    if word.endswith('ing'):
        return word[:-3] + 'in'
    return word
def stem_words(text):
    words = re.split('\W+', text)
    stemmed_words = [porter_stemmer(word) for word in words]
    return ' '.join(stemmed_words)
