    mid = (low + high) // 2
    if arr[mid] == target:
        return mid
    elif arr[mid] < target:
        return recursive_binary_search(arr, target, low=mid+1, high=high)
    else:
        return recursive_binary_search(arr, target, low=low, high=mid-1)

