def word_counter(words):
    word_dict = {}
    total_word_count = 0
    for word in words:
        if word in word_dict:
            word_dict[word] += 1
        else:
            word_dict[word] = 1
        total_word_count += 1
    return {word: (word_dict[word], total_word_count) for word in word_dict}
# Example usage
