print(selection_sort(arr))  # Output: [1, 2, 3, 4, 5, 6, 8]

def bubble_sort(arr):
    n = len(arr)
    for i in range(n-1):
        for j in range(0, n-i-1):
            if arr[j] > arr[j+1]:
                arr[j], arr[j+1] = arr[j+1], arr[j]
    return arr
# Example usage
arr = [5, 2, 8, 3, 1, 6, 4]
print(bubble_sort(arr))  # Output: [1, 2, 3, 4, 5, 6, 8]

