def non_recursive_heapsort(arr):
    n = len(arr)
    for i in range(n // 2, n):
        sink(arr, i)
    for i in range(n // 2, n):
        swap(arr, 0, i)
        sink(arr, 0)
    return arr
def sink(arr, i):
    while i * 2 <= n - 1:
        max_idx = i * 2
        if max_idx < n and arr[max_idx] < arr[max_idx + 1]:
            max_idx += 1
        if i == max_idx:
            break
        swap(arr, i, max_idx)
        i = max_idx
def swap(arr, i, j):
    arr[i], arr[j] = arr[j], arr[i]
