class Node:
    def __init__(self, val):
        self.val = val
        self.left = None
        self.right = None

def first_common_ancestor(root1, root2):
    """
    Returns the first common ancestor between the two binary trees.
    """
    if root1 is None or root2 is None:
        return None
    if root1.val == root2.val:
        return root1
    if root1.left is None and root2.left is None:
        return root1
    if root1.right is None and root2.right is None:
        return root1
    if root1.left is not None and root2.right is not None:
        return first_common_ancestor(root1.left, root2.right)
    if root1.right is not None and root2.left is not None:
        return first_common_ancestor(root1.right, root2.left)
    return None

# Test the function
